mapper.update:
update community_action
set cg_like = 'like'
where mb_id = #{mb_id} and cg_type = #{cg_type} and cg_num = #{cg_num}

update community_action
set cg_like = ''
where mb_id = #{mb_id} and cg_type = #{cg_type} and cg_num = #{cg_num}

update community_action
set cg_report = 'report'
where mb_id = #{mb_id} and cg_type = #{cg_type} and cg_num = #{cg_num}

update community_action
set cg_report = ''
where mb_id = #{mb_id} and cg_type = #{cg_type} and cg_num = #{cg_num}
